nestle.product.templates.ManagementTemplate='<tpl if="managementInfo && managementInfo.length">    <div class="management_container product_data_block">        <h1><b>Indication/Description<tpl if="managementFootnoteSymbol">{managementFootnoteSymbol}</tpl></b></h1>        <table>            <tpl for="managementInfo">                <tpl if="Ext.isArray(values)">                    <tr>                        <tpl for="values">                            <tr>                                <td style="vertical-align: text-top;">&nbsp;&nbsp;&nbsp;&nbsp;&mdash;</td>                                <td><span>{.}</span></td>                            </tr>                        </tpl>                    </tr>                </tpl>                <tpl if="!Ext.isArray(values)">                    <tr>                        <td style="vertical-align: text-top;"><img src="images/blue_dot.jpg"/></td>                        <td><span>{.}</span></td>                    </tr>                </tpl>            </tpl>        </table>    </div></tpl><tpl if="managementNotes">    <br/>    <ul class="nut_notes">        <tpl for="managementNotes">            <li><i>{.}</i></li>        </tpl>    </ul><br/></tpl><tpl for="alerts">    <span class="alert">{.}</span><br/><br/></tpl>';
